import styled from 'styled-components';
import { darken } from 'polished';

export const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;

  button {
    padding: 20px;
    border: 0;
    /* border-radius: 4px; */
    overflow: hidden;
    margin-top: auto;
    font-size: 20px;
    text-align: center;
    color: #fff;
    background: #d46683;

    display: flex;
    align-items: center;
    transition: background 0.2s; // Tempo que demora para escurecer o background

    &:hover {
      background: ${darken(0.03, '#d46683')}; //Escurecer o bot√£o
    }
  }
`;
