import styled, { keyframes } from 'styled-components';
import { darken } from 'polished';

export const Container = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  button {
    padding: 20px;
    border: 0;
    font-size: 20px;
    font-weight: bold;
    color: #fff;
    background: #2272a1;

    display: flex;
    align-items: center;
    transition: background 0.2s; // Tempo que demora para escurecer o background

    &:hover {
      background: ${darken(0.03, '#2272a1')}; //Escurecer o bot√£o
    }
  }
`;

const morph = keyframes`
  0% {margin-bottom: 0px;}
  50% {margin-bottom: 50px;}
  100% {margin-bottom: 0px;}
`;

export const Logo = styled.div`
  display: flex;
  height: 200px;
  align-items: center;
  padding: 20px;

  span {
    font-family: Roboto, sans-serif;
    font-size: 100px;
    font-weight: 400;
    margin: 0px 20px;
    color: #333;
  }

  div {
    height: 70px;
    width: 70px;
    border-radius: 50%;
    margin: 0 auto;
    background: #2272a1;
    animation: ${morph} 2s infinite;
  }
`;
