import styled from 'styled-components';
import { darken } from 'polished';

export const Container = styled.div`
  /* display: flex; */
  flex-direction: column;
  /* justify-content: center; */
  /* align-items: center; */
  background: #333;

  button {
    padding: 20px;
    border: 0;
    margin-top: auto;
    font-size: 20px;
    text-align: center;
    color: #fff;
    background: #2272a1;

    display: flex;
    align-items: center;
    transition: background 0.2s; // Tempo que demora para escurecer o background

    &:hover {
      background: ${darken(0.03, '#2272a1')}; //Escurecer o bot√£o
    }
  }
`;
