import styled from 'styled-components';
import { darken } from 'polished';

export const Container = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  button {
    padding: 20px;
    border: 0;
    font-size: 20px;
    color: #fff;
    background: #2272a1;

    display: flex;
    align-items: center;
    transition: background 0.2s; // Tempo que demora para escurecer o background

    &:hover {
      background: ${darken(0.03, '#2272a1')}; //Escurecer o bot√£o
    }
  }
`;

export const Logo = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 20px;
  background: #f00;

  h1 {
    font-family: 'Roboto';
    font-size: 100px;
    font-weight: 400;
    color: #333;
  }

  div {
    height: 50px;
    width: 50px;
    border-radius: 25px;
    overflow: hidden;
    background: #2272a1;
  }
`;
